import React, { Component } from 'react';
import './rightModule.scss';
import $ from 'jquery';
import "animate.css";
import { TwitterTimelineEmbed, TwitterShareButton, TwitterFollowButton, TwitterHashtagButton, TwitterMentionButton, TwitterTweetEmbed, TwitterMomentShare, TwitterDMButton, TwitterVideoEmbed, TwitterOnAirButton } from 'react-twitter-embed';
import InstagramEmbed from 'react-instagram-embed';
import ReactPlayer from 'react-player';
import Carousel from 'nuka-carousel';

class RightModule extends Component {
  constructor() {
  super();
  this.state = {
    video:[]
  };


}
    componentDidMount() {
      console.log(localStorage);
var myFetchOptions = {
  method: 'GET'
};
fetch("https://api.americanmuslimtoday.net/amt-news/api/v1/news?page=1&category=169346d7-67d6-4be6-864e-6ceabea69a27", myFetchOptions)
.then(response => response.json())
.then(json => {
  console.log(json.list);
  var tmp_list=json.list;
  var tmp_video='';
  var video_arr=[]; // 保存所有video字符串id得数组
this.setState({video: json.list})  // videoarry 后面取值便利渲染
});
        this.scrollFadeIn();
    }

    scrollFadeIn() {
        let num = 0,
            topArr = [];

        $(".right-module").find(".module-box").each(function(){
            topArr.push($(this).offset().top - $(window).height() + 25);
        });

        $(window).scroll(res => {
            if($(window).scrollTop() > 20){
                $(".right-module").find(".module-box").eq(0).addClass('fadeInRight');
            }
            if(num < topArr.length && $(window).scrollTop() > topArr[num]){
                $(".right-module").find(".module-box").eq(num).addClass('fadeInRight');
                num += 1;
            }
        })
    }

    render() {
        return (
            <div className="right-module">

            <div className="module-box animated">
                <div className="module-title" style={{marginTop: 20}}>
                    <svg className="icon" aria-hidden="true">
                        <use xlinkHref="#icon-Yahoo"></use>
                    </svg>
                    <span className="title-text" style={{color:'#6001D2'}}>Video</span>
                </div>
                <div className="news-block">

<Carousel>

{
this.state.video.map((item, index)=>{
return(
  <div className='player-wrapper'>
        <ReactPlayer
          className='react-player'
          url={item.video}
          width='100%'
          height='100%'
          controls='true'

        />
  </div>

)

})
}
</Carousel>
                </div>
            </div>




                <div className="module-box animated">
                    <div className="module-title">
                        <svg className="icon" aria-hidden="true">
                            <use xlinkHref="#icon-facebook"></use>
                        </svg>
                        <span className="title-text" style={{color:'#15549B'}}>Facebook</span>
                    </div>
                    <div className="news-block">
                        {/* <a href="javascript:;" className="toLink">
                            <img src={require('../../../assets/images/one.jpg')} alt=""/>
                        </a>
                        */}



                      <iframe src="https://www.facebook.com/plugins/page.php?href=https%3A%2F%2Fwww.facebook.com%2FAmericanMuslimToday%2F&tabs=timeline&width=300&height=390&small_header=true&adapt_container_width=true&hide_cover=false&show_facepile=true&appId" width="300" height="390" style={{border:"none",overflow:"hidden"}} scrolling="no" frameborder="0" allowTransparency="true" allow="encrypted-media"></iframe>


                    </div>
                </div>

                <div className="module-box animated">
                    <div className="module-title" style={{marginTop: 20}}>
                        <svg className="icon" aria-hidden="true">
                            <use xlinkHref="#icon-twitter"></use>
                        </svg>
                        <span className="title-text" style={{color:'#078BC9'}}>Twitter</span>
                    </div>
                    <div className="news-block">
                    <TwitterTimelineEmbed
                        sourceType="profile"
                        screenName="AmericanMuslimT"
                        options={{height: 350}}
                        />
                    </div>
                </div>

                <div className="module-box animated">
                    <div className="module-title" style={{marginTop: 20}}>
                        <svg className="icon" aria-hidden="true">
                            <use xlinkHref="#icon-githair-instagram"></use>
                        </svg>
                        <span className="title-text" style={{color:'#CE4A95'}}>instagram</span>
                    </div>
                    <div className="news-block">
                    <div className="insBlock">
                    <InstagramEmbed
                      url='https://www.instagram.com/p/B5gFd3qpp4R/'
                      maxWidth={200}

                      hideCaption={false}
                      containerTagName='div'
                      protocol=''
                      injectScript
                      onLoading={() => {}}
                      onSuccess={() => {}}
                      onAfterRender={() => {}}
                      onFailure={() => {}}
                      />
                        </div>
                    </div>
                </div>

                <div className="module-box animated">
                    <div className="module-title" style={{marginTop: 20}}>
                        <svg className="icon" aria-hidden="true">
                            <use xlinkHref="#icon-Yahoo"></use>
                        </svg>
                        <span className="title-text" style={{color:'#6001D2'}}>Yahoo</span>
                    </div>
                    <div className="news-block">

  <Carousel>

  {
  this.state.video.map((item, index)=>{
    return(
      <div className='player-wrapper'>
            <ReactPlayer
              className='react-player'
              url={item.video}
              width='100%'
              height='100%'
              controls='true'

            />
      </div>

    )

  })
}
</Carousel>
                    </div>
                </div>

                <div className="module-box animated">
                    <div className="module-title" style={{marginTop: 20}}>
                        <svg className="icon" aria-hidden="true">
                            <use xlinkHref="#icon---"></use>
                        </svg>
                        <span className="title-text" style={{color:'#000000'}}>Sports</span>
                    </div>
                    <div className="news-block">
                        <a href="javascript:;" className="toLink">
                            <img src={require('../../../assets/images/one.jpg')} alt=""/>
                        </a>
                    </div>
                </div>
            </div>
        )
    }
}

export default RightModule
