import React, { Component } from 'react';
import './headerNav.scss';
import $ from 'jquery';
import { Input, Button , Menu} from 'antd';

import { Router, Route, Link, browserHistory } from 'react-router-dom';
const SubMenu = Menu.SubMenu;

const MenuItemGroup = Menu.ItemGroup;
const { Search } = Input;
class HeaderNav extends Component {
  constructor (props) {
    super(props)

    this.state = {
      category: [],
      	current: 'top',
    }
  }
    componentDidMount() {
// request for all category
var myFetchOptions = {
  method: 'GET'
};
fetch("https://api.americanmuslimtoday.net/amt-news/api/v1/news/categories?showArchived=true", myFetchOptions)
.then(response => response.json())
.then(json => {

  var tmp_list = json;

  for (var i=0 ; i < tmp_list.length; i++){
    if (tmp_list[i].category=="Advertisement1"){
       tmp_list.splice(i,1);
    }
    if (tmp_list[i].category=="Advertisement2"){
       tmp_list.splice(i,1);
    }
    if (tmp_list[i].category=="Advertisement3"){
       tmp_list.splice(i,1);
    }
    if (tmp_list[i].category=="Logo-video"){
       tmp_list.splice(i,1);
    }
      if (tmp_list[i].category=="Video"){
         tmp_list.splice(i,1);
      }


  }
  this.setState({category: tmp_list})



});



// 处理头部

        $(window).scroll(res => {
            if($(window).scrollTop() > 10){
                $(".header-nav").css({
                    'box-shadow': '0 0 10px 0 rgba(0,0,0,.4)',
                    '-webkit-box-shadow': '0 0 10px 0 rgba(0,0,0,.4)',
                    '-moz-box-shadow': '0 0 10px 0 rgba(0,0,0,.4))',
                    '-o-box-shadow': '0 0 10px 0 rgba(0,0,0,.4)'
                })
            } else {
                $(".header-nav").css({
                    'box-shadow': 'none',
                    '-webkit-box-shadow': 'none',
                    '-moz-box-shadow': 'none',
                    '-o-box-shadow': 'none'
                })
            }
        })
    }
    handleSearch = value => {
      this.props.passSearch(value)
    };

    render() {
      console.log(this.props);
      const {category} = this.state
      const newsCatelist =  category.map((newsItem, index) => (
      <li> <a> <span href="javascript:;" className="amt-font">AMT</span> <span href="javascript:;" >{newsItem.category}</span> </a> </li>

  ))
        return (
            <div className="header-nav">

                <div className="nav-important">
                    <Link to={``} target="_blank">
                    <div className="logo">
                        <img src={require('../../../assets/images/logo.png')}/>
                    </div>
                     </Link>

                    <div className="search-block">
                    <Search
                        placeholder="Please enter your search"
                        enterButton="Search"
                        size="large"
                    //    onSearch={value => console.log(value)}
                        onSearch={value => this.handleSearch(value)}
                    //    onSearch={this.props.passSearch.bind(this,'aaa')}
                    />
                    </div>
                    <div className="login-module">
                        <Button type="link" className="register" shape="round" ghost size={'large'} >Register</Button>
                        <Button type="primary" className="login" shape="round" size={'large'}>Login</Button>
                    </div>
                </div>
                <div className="nav-text">
                  {/*  <ul>
                        {newsCatelist}

                       <li> <a> <span href="javascript:;" className="amt-font">AMT</span> <span href="javascript:;" >News</span> </a> </li>

                    </ul>

                  */}


				<Menu mode="horizontal" onClick={this.handleClick.bind(this)} selectedKeys={[this.state.current]} class="register" >
			{/*		<Menu.Item key="top" >
						<Icon type="appstore"/>Recommand
					</Menu.Item>
					<Menu.Item key="shehui">
						<Icon type="appstore"/>Video
					</Menu.Item>
					<Menu.Item key="guonei">
						<Icon type="appstore"/>Facebook
					</Menu.Item>
					<Menu.Item key="yule1">
						<Icon type="appstore"/>Instagram
					</Menu.Item>
					<Menu.Item key="yule">
						<Icon type="appstore"/>Instagram
					</Menu.Item>
			*/}


					<Menu.Item key="shishang" >
					<Link to={`classified/`} target="_blank">
						<Icon type="appstore"/>Classified
					</Link>
					</Menu.Item>


					{/*
					<Menu.Item key="tiyu">
						<a target="_blank" href='https://app.americanmuslimtoday.net/login'>
						<Icon type="appstore"/>Social Login
						</a>
					</Menu.Item>
					*/}


					{userShow}
				</Menu>
                </div>

            </div>

        )
    }
}

export default HeaderNav
