import React from 'react';
import {Row, Col, BackTop} from 'antd';

import {Tabs, Carousel} from 'antd';
import {Card} from 'antd';
//import PCNewsBlock from './pc_news_block';
//import CommonComments from './common_comments';
import $ from 'jquery';


import HeaderNav from '@/components/home/header/headerNav';
import Header from '@/components/home/header/header';
import LeftModule from '@/components/home/content/leftModule';
import CenterModule from '@/components/home/content/centerModule';
import RightModule from '@/components/home/content/rightModule';
import Footer from '@/components/home/footer/footer';

const TabPane = Tabs.TabPane;
export default class PCNewsDetails extends React.Component {
  constructor() {
		super();
		this.state = {
			newsItem: ''
		};
	};
	componentDidMount() {
    // 处理头部黏住
    $(window).scroll(res => {
      // console.log($(window).scrollTop());

    //  var headerHeight = $(".header").height() + parseInt($(".header").css('margin-bottom'));
      if($(window).scrollTop() > 0){
        $(".header-nav").css({
          'position': 'fixed',
          'top': '0',
          'padding':'5px',
          'height':'70px'
        });
        $(".nav-text").css({
          'display': 'none'
        });



      } else {
        $(".header-nav").css({
          'position': 'relative',
          'top': 'unset',
        });
      }
    })




		console.log(this.props.match.params);
		var myFetchOptions = {
			method: 'GET'
		};
		fetch("https://api.americanmuslimtoday.net/amt-news/api/v1/news/" + this.props.match.params.newsId, myFetchOptions).then(response => response.json()).then(json => {
			this.setState({newsItem: json});
			document.title = this.state.newsItem.headline + "American Muslim Today news";
		});
	};
	createMarkup() {
		return {__html: this.state.newsItem.fullNews};
	};
	render() {
		return (
			<div>

				<HeaderNav></HeaderNav>
				<Row>
					<Col span={2}></Col>
					<Col span={14} className="container">
					<div style={{marginBottom: "20px",fontSize:"27px",lineHeight:"1.1" ,letterSpacing:"-.016em",background:"blue"}}>

						<h1>{this.state.newsItem.headline}</h1>
					</div>

						<img alt="" src={this.state.newsItem.bannerImage} style={{margin:"20px" , width: "760px"}} />
						<div class="articleContainer" dangerouslySetInnerHTML={this.createMarkup()}></div>
						<hr/>


					</Col>
					<Col span={1}></Col>
					<Col span={7}>




					/*这里放所有谷歌的new */

					</Col>

				</Row>
				<Footer></Footer>
				<BackTop/>

			</div>
		);
	};
}
