import React, { Component } from 'react';
import $ from 'jquery';
import style from './header.module.scss';

class Header extends Component {
    constructor(){
        super();
        this.state = {
            isOpen: false
        }
    }

    menuToggle = () => {
        this.setState(state => ({
            isOpen: !state.isOpen
        }))
    }

    render() {
        return (
            <div id="header" className={style.mobileheader}>
                <div className={style.logo}>
                    <img src={require('assets/images/logo.png')}/>
                </div>

                <svg style={{width: 30,height: 30}} onClick={this.menuToggle}>
                    <use xlinkHref="#icon-more"></use>
                </svg>

                <div className={[`${style.menulist}`, 'headerMenubox'].join(' ')} style={this.state.isOpen == true ? {opacity: 1} : {opacity: 0}}>
                    <a href="javascript:;" className={style.menu} onClick={this.menuToggle}>
                        <span>首页</span>
                    </a>
                    <a href="javascript:;" className={style.menu} onClick={this.menuToggle}>
                        <span>登陆</span>
                    </a>
                    <a href="javascript:;" className={style.menu} onClick={this.menuToggle}>
                        <span>华人论坛</span>
                    </a>
                    <a href="javascript:;" className={style.menu} onClick={this.menuToggle}>
                        <span>商家点评</span>
                    </a>
                </div>
            </div>
        )
    }
}

export default Header
